import React, { Component } from 'react'
import axios from 'axios';

export default class CreateUser extends Component {
    state = {
        listUser : []
    }
   async componentDidMount(){
       const res = await axios.get("http://localhost:4000/api/users");
       this.setState({listUser: res.data});
       console.log(this.state.listUser);
    }

    //metodo onChange para capturar el input que almacera el valor de usar name en la bd
    onChangeUserName = (e) =>{
        console.log(e.target.value);
    }

    render() {
        return (
           <div className="row">
               <div className="col-md-4">
                  <div className="card card-body">
                      <h3>Crear nuevo usuario</h3>
                    <form action="">
                        <div className="form-group">
                            <input type="text" className="form-control"
                                onChange={this.on}
                            />
                        </div>
                    </form>
                  </div>
               </div>
               <div className="col-md-8">
                   <ul className="list-group">
                       {
                           this.state.listUser.map(user => (
                           <li key={user._id} className="list-group-item list-group-item-action"> 
                           {user.userName}

                           </li>
                            ))
                            
                       }
                   </ul>
               </div>
           </div>
            
        )
    }
}
